import { useState, useEffect } from "react"

const Pokedex = () => {
    const [pokemon, setPokemon] = useState([])
    const [sprites, setSprites] = useState([])

    useEffect(() => {
        const fetchPokemon = async () => {
            let pokemonList;
            const url = 'https://pokeapi.co/api/v2/pokemon?limit=150&offset=0';
            try {
                const res = await fetch(url);
                pokemonList = await res.json();
                setPokemon(pokemonList.results);
            } catch (e) {
                console.log('error: ', e)
            }
        }
        const fetchSprites = async () => {
            let pokemonSprites;
            pokemon.forEach(async () => {
                const spriteRes = await fetch(pokemon.url);
                pokemonSprites = await spriteRes.json();
                console.log('ps',pokemonSprites)
                setSprites(pokemonSprites);
            })
        }
        fetchPokemon()
        if (pokemon.length > 0) fetchSprites();
    }, [])

    return (
        <>
            Pokedex!
            {pokemon.map((poke, i) => {
                return (<div key={i}><p>{poke.name}</p>
                <img src={''}/>
                </div>)
            })}
        </>
    )
}
export default Pokedex;
