import { useState, useEffect } from "react"

const fetchSprites = async (url) => {
    let spriteList;
    try {
        const spRes = await fetch(url)
        spriteList = await spRes.json()
    } catch (e) {
        console.log('error: ', e)
    }
    return spriteList;
}

const Pokedex = () => {
    const [pokemon, setPokemon] = useState([])

    useEffect(() => {
        const fetchPokemon = async () => {
            let pokemonList;
            let pokemonData;
            const url = 'https://pokeapi.co/api/v2/pokemon?limit=150&offset=0';
            try {
                const res = await fetch(url);
                const data = await res.json();
                pokemonList = data.results;
                pokemonList.map(async () => {
                    const resTwo = await fetch(pokemonList.url);
                    pokemonData = await resTwo.json();
                })
                console.log('list', pokemonData)
                /*
                console.log(pokemonList)
                //setPokemon(pokemonList.results);*/
            } catch (e) {
                console.log('error: ', e)
            }
        }
        fetchPokemon()
    }, [])

    return (
        <>
            Pokedex!
            {pokemon.map((poke, i) => {
                return (<div key={i}><p>Pokemon</p>
                </div>)
            })}
        </>
    )
}
export default Pokedex;
