import { useState, useEffect } from "react"

const Pokedex = () => {
    const [pokemon, setPokemon] = useState([])

    useEffect(() => {
        const fetchPokemon = async () => {
            let pokemonList;
            const url = 'https://pokeapi.co/api/v2/pokemon?limit=150&offset=0';
            try {
                const res = await fetch(url);
                pokemonList = await res.json();
                setPokemon(pokemonList.results);
            } catch (e) {
                console.log('error: ', e)
            }
        }
        const fetchSprites = async (url) => {
            let spriteList;
            try {
                const spRes = await fetch(poke.url)
                spriteList = await spRes.json()
            } catch (e) {
                console.log('error: ', e)
            }
            return spriteList;
        }
        fetchPokemon()
    }, [])

    return (
        <>
            Pokedex!
            {pokemon.map(async(poke, i) => {

                return (<div key={i}><p>{poke.name}</p>
                <img src={fetchSprites(poke.url)}/>
                </div>)
            })}
        </>
    )
}
export default Pokedex;
